import React from 'react'
import {useState} from 'react'
import Dashboard from './Dashboard'
import { BrowserRouter, Route, Routes, Link } from 'react-router-dom';

function Login () {
    const [namelog, setNamelog] = useState('')
    const [passwordlog, setPasswordlog] = useState('')
    const [flag, setFlag] = useState(false)
    const [home, setHome] = useState(true)

    function handleLogin(e) {
        e.preventDefault();

        let user = localStorage.getItem("Name").replace(/"/g,"");
        let pass = localStorage.getItem("Password").replace(/"/g,"");


        if (!namelog || !passwordlog) {
            setFlag(true);
            console.log("Empty");
        } else if (passwordlog !== pass || namelog !== user){
            setFlag(true)
        } else {
            setHome(!home)
            setFlag(false);
        }

    }

  return (
    <div>

        {home ? (

<div className="App content-center grid h-screen place-items-center">
<div>
  {/* <span className="w-8 h-8 text-center"> */}
  <img className="w-8 h-8 inline" src="data:image/png;base64," />
  {/* </span> */}
  <h3 className="font-bold">Log in to Twitter</h3>

<div className="w-full max-w-2xl">
<form onSubmit={handleLogin}
className="bg-white shadow-md rounded px-8 pt-6 pb-8 mb-4 ">
<div className="mb-4">
<label className="block text-gray-700 text-sm font-bold mb-2" for="username">
Username
</label>
<input className="shadow appearance-none border rounded w-full py-2 px-3
text-gray-700 leading-tight focus:outline-none focus:shadow-outline" id="username" type="text" placeholder="Username"
onChange={(event) => setNamelog(event.target.value)}
/>
</div>
<div className="mb-6">
<label className="block text-gray-700 text-sm font-bold mb-2" for="password">
Password
</label>
<input className="shadow appearance-none border border-red-500 rounded w-full py-2 px-3
text-gray-700 mb-3 leading-tight focus:outline-none focus:shadow-outline" id="password" type="password" placeholder="******************"
onChange={(event) => setPasswordlog(event.target.value)} />
<p className="text-red-500 text-xs italic">Please choose a password.</p>
</div>
{flag && (
        <alert>
            Please fill correct info
        </alert>
    )}
<div className="flex items-center justify-between">
<button className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline w-72" type="submit">
Sign In
</button>


</div>


<Link to="/register">
<a class="text-center text-gray-500 text-xs underline">
Didn't have an account? Register
</a>
</Link>
</form>
{/* <Link to="/register">
<a class="text-center text-gray-500 text-xs underline">
Didn't have an account? Register
</a>
</Link> */}


{/* <a class="text-center text-gray-500 text-xs">
Didn't have an account? Register
</a> */}
</div>

</div>
</div>

