        //     <input 
        //     type='text'
        //     placeholder='enter your name'
        //     onChange={(event) => setNamelog(event.target.value)}
        //     />

        //     <label> Password </label>
        //     <input 
        //     type='Password'
        //     placeholder='Password...'
        //     onChange={(event) => setPasswordlog(event.target.value)}
        //     />

        //     <button>
        //         Login
        //     </button>

        //     {flag && (
        //         <alert>
        //             Please fill correct info
        //         </alert>
        //     )}
        // </form>
        ) : (
            <Dashboard />
        )}

    </div>
  )
            }
