        //     placeholder='Password...'
        //     onChange={(event) => setPassword(event.target.value)}
        //     />

        //     <button>
        //         Register
        //     </button>

        //     {flag && (
        //         <alert>
        //             Please fill every field
        //         </alert>
        //     )}
        // </form>
        ) : (
            <Login />
        )}
    </div>
  )
}

export default Registration
