import React from 'react'
import { useState, useRef } from 'react'

function Home() {

  const [tweets, setTweets] = useState([]);
  const [currentTweet, setCurrentTweet] = useState("");
  const inputTweet = useRef(null);

  const addTask=() => {
    setTweets([...tweets, currentTweet]);
    console.log(tweets);
    inputTweet.current.value = "";
    setCurrentTweet("");
  }

  const deleteTweet = (tweetToDelete) => {
    setTweets(tweets.filter((tweet) => {
      return tweet !== tweetToDelete
    }))
  }

  return (
    <div className="flex mx-96 h-full">
      <div>
 <img className="w-12 h-8 m-2" src="data:image/png;base64," />

<aside class="w-64" aria-label="Sidebar">
