import React from 'react'
import { useState, useRef } from 'react'

function Home() {

  const [tweets, setTweets] = useState([]);
  const [currentTweet, setCurrentTweet] = useState("");
  const inputTweet = useRef(null);

  const addTask=() => {
    setTweets([...tweets, currentTweet]);
    console.log(tweets);
    inputTweet.current.value = "";
    setCurrentTweet("");
  }

  const deleteTweet = (tweetToDelete) => {
    setTweets(tweets.filter((tweet) => {
      return tweet !== tweetToDelete
    }))
  }

  return (
    <div className="flex mx-96 h-full">
      <div>
 <img className="w-12 h-8 m-2" src="data:image/png;base64," />

<aside class="w-64" aria-label="Sidebar">
   <div class=" inline overflow-y-auto py-4 px-3 bg-gray-50 rounded dark:bg-gray-800">
      <ul class="space-y-2">
         <li>
         <a>
    <svg xmlns="http://www.w3.org/2000/svg" class="m-2 inline h-6 w-6" fill="none" viewBox="0 0 24 24" stroke="currentColor" stroke-width="2">
  <path stroke-linecap="round" stroke-linejoin="round" d="M3 12l2-2m0 0l7-7 7 7M5 10v10a1 1 0 001 1h3m10-11l2 2m-2-2v10a1 1 0 01-1 1h-3m-6 0a1 1 0 001-1v-4a1 1 0 011-1h2a1 1 0 011 1v4a1 1 0 001 1m-6 0h6" />
</svg> <span className="cursor-pointer hover:text-sky-500"> Home </span>
</a>
<br></br>
<a>
<svg xmlns="http://www.w3.org/2000/svg" class="m-2 inline h-6 w-6" fill="none" viewBox="0 0 24 24" stroke="currentColor" stroke-width="2">
  <path stroke-linecap="round" stroke-linejoin="round" d="M7 20l4-16m2 16l4-16M6 9h14M4 15h14" />
</svg> <span className="cursor-pointer hover:text-sky-500"> Explore </span>

</a>
<br></br>

<a>

<svg xmlns="http://www.w3.org/2000/svg" class="m-2 inline h-6 w-6" fill="none" viewBox="0 0 24 24" stroke="currentColor" stroke-width="2">
  <path stroke-linecap="round" stroke-linejoin="round" d="M15 17h5l-1.405-1.405A2.032 2.032 0 0118 14.158V11a6.002 6.002 0 00-4-5.659V5a2 2 0 10-4 0v.341C7.67 6.165 6 8.388 6 11v3.159c0 .538-.214 1.055-.595 1.436L4 17h5m6 0v1a3 3 0 11-6 0v-1m6 0H9" />
</svg> <span className="cursor-pointer hover:text-sky-500"> Notifications </span>

</a>

<br></br>
<a>

<svg xmlns="http://www.w3.org/2000/svg" class="m-2 inline h-6 w-6" fill="none" viewBox="0 0 24 24" stroke="currentColor" stroke-width="2">
  <path stroke-linecap="round" stroke-linejoin="round" d="M8 10h.01M12 10h.01M16 10h.01M9 16H5a2 2 0 01-2-2V6a2 2 0 012-2h14a2 2 0 012 2v8a2 2 0 01-2 2h-5l-5 5v-5z" />
</svg> <span className="cursor-pointer hover:text-sky-500"> Messages </span>

</a>
<br></br>


<a>

<svg xmlns="http://www.w3.org/2000/svg" class="m-2 inline h-6 w-6" fill="none" viewBox="0 0 24 24" stroke="currentColor" stroke-width="2">
  <path stroke-linecap="round" stroke-linejoin="round" d="M5 5a2 2 0 012-2h10a2 2 0 012 2v16l-7-3.5L5 21V5z" />
</svg> <span className="cursor-pointer hover:text-sky-500"> Bookmarks </span>

</a>

         </li>
      </ul>
   </div>
</aside>
    </div>
    <div className=" m-4 w-96 h-{1000}">
      <h2 className="font-bold"> Latest tweets </h2>
     <input 
     ref={inputTweet}
     onKeyDown={(event) => {
      if (event.keyCode === 13)
      addTask();
    }}
     onChange={(event) => setCurrentTweet(event.target.value)}
     className="w-full mt-16 h-32 border-inherit border-2" type="text" maxlength = "140" placeholder="What's happening?" />
     <button onClick={addTask}
      className="bg-sky-500 text-white text-bold py-2 px-4 mt-4 rounded-full"> Tweet </button>

      <ul>
        {tweets.map((val, key) => {
          return(
            <div >
             <li className="mt-16 h-32 border-inherit border-2" key={key}> {val} </li>
             <button className="bg-sky-500 text-white text-bold py-2 px-4 mt-4 rounded-full" onClick={() => deleteTweet(val)}> Delete Tweet </button>
             </div>
          )
        })}
      </ul>
    </div>
    </div>
  )
}

export default Home
